import React, { useEffect, useState } from 'react';
import API from '../api';


function RoomList() {
  const [rooms, setRooms] = useState([]);

useEffect(() => {
  API.get('rooms/')
    .then(res => {
      console.log('ROOMS:', res.data);  // ✅ Add this
      setRooms(res.data);
    })
    .catch(err => {
      console.error('ROOM FETCH ERROR:', err);  // ✅ Add this
    });
}, []);


  return (
    <div style={{ padding: '1rem' }}>
      <h2>Available Rooms</h2>
      {rooms.length === 0 ? (
        <p>No rooms available.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {rooms.map(room => (
            <li key={room.id} style={{ margin: '10px 0', padding: '10px', border: '1px solid #ccc' }}>
              <strong>{room.name}</strong><br />
              Capacity: {room.capacity}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default RoomList;
